import { createSlice } from "@reduxjs/toolkit";

export const postSlice = createSlice({
    name: "posts",
    initialState: {
        posts: null,
    },
    reducers: {
        getPosts: (state, {payload}) => {
            state.posts = "coucou"
        }
    },
})

export const { getPosts } = postSlice.actions
export default postSlice.reducer
